<style lang="scss">
  $time: .5s;
  #space3D {
    perspective: 800px;
    perspective-origin: 50% 50%;
    background-color: transparent;
    z-index: 11;
    position: fixed;
    left: 0;
    top: 0;
    height: 100%;
    width: 100%;

    &.space-leave {
      visibility: hidden;
    }

    &.space-enter {
      visibility: visible;
    }

    .sign-modal-wrap {
      transform-style: preserve-3d;
      z-index: 12;

      .sign-modal {
        padding: 50px 60px;
        background-color: #fff;
        border-radius: 4px;
        position: fixed;
        top: 50%;
        left: 50%;
        width: 500px;
        height: 460px;
        margin-left: -250px;
        transition: $time;
        font-size: 14px;
        visibility: hidden;
        opacity: 0;
        display: flex;
        flex-direction: column;

        input[type="text"], input[type="password"], input[type="email"] {
          width: 100%;
          height: 40px;
          color: #a5a5a5;
          font-size: 12px;
          padding: 8px 12px;
          border-radius: 4px;
          border: 1px solid #e5e9ef;
        }

        input[type=checkbox] {
          margin-left: 5px;
        }

        button {
          height: 40px;
          width: 100%;
          color: #fff;
          border-radius: 3px;
          background-color: $color-blue;
          font-weight: bold;

          &:hover {
            background-color: $color-blue-hover;
          }
        }

        >div {
          margin-top: 15px;
        }

        span {
          margin-top: 15px;
          display: flex;
          align-items: center;
          justify-content: space-between;
          flex: 1;
        }

        &.sign-up-show,
        &.sign-in-show {
          visibility: visible;
          opacity: 1;
          transform: rotateY(0deg) translateY(-200px);
        }

        &.sign-up-zero,
        &.sign-in-zero {
          visibility: hidden;
          opacity: 0;
          transform: rotateY(0deg) translateY(-300px);
        }

        &.sign-in-turn,
        &.sign-up-turn {
          visibility: hidden;
          opacity: 0;
        }

        &.sign-in-turn {
          transform: rotateY(-180deg) translateY(-200px);
        }

        &.sign-up-turn {
          transform: rotateY(180deg) translateY(-200px);
        }
      }
    }
  }
</style>

<template>
  <div id="space3D"
       :class="[ showModal ? 'space-enter' : 'space-leave' ]"
       @click="hiddenSign">
    <div class="sign-modal-wrap abs" ref="wrap">
      <div class="sign-modal"
           :class="{
            'sign-in-show' : showSignIn && !showSignUp,
            'sign-in-zero' : !showSignIn && !showSignUp,
            'sign-in-turn' : !showSignIn && showSignUp
           }" @click.stop>
        <img src="data:image/jpeg;base64,">
        <div>
          <input name="username" type="text" v-model="signIn.name" placeholder="邮箱" @keyup.enter="login">
        </div>
        <div>
          <input name="password" type="password" v-model="signIn.password" placeholder="密码" @keyup.enter="login">
        </div>
        <span>
          <label>记住我<input type="checkbox" :checked="signIn.remember" v-model="signIn.remember"></label>
          <em></em>
        </span>
        <div>
          <button @click="login">登录</button>
        </div>
        <span>
          <a>忘记密码?></a>
          <a @click="showRegister">点击注册»</a>
        </span>
      </div>
      <div class="sign-modal"
           :class="{
            'sign-up-show' : showSignUp && !showSignIn,
            'sign-up-zero' : !showSignUp && !showSignIn,
            'sign-up-turn' : !showSignUp && showSignIn
           }" @click.stop>
        <img src="data:image/jpeg;base64,">
        <div>
          <input type="text" v-model="signUp.name" placeholder="昵称（2-12个字符组成，1个汉字占2个字符）">
        </div>
        <div>
          <input type="email" v-model="signUp.email" placeholder="邮箱（填写常用邮箱，用于登录）">
        </div>
        <div>
          <input type="password" v-model="signUp.password" placeholder="密码（6-16个字符组成，区分大小写）">
        </div>
        <span>
          <div ref="captcha"></div>
          <em></em>
        </span>
        <span>
          <a></a>
          <a @click="showLogin">已有账号»</a>
        </span>
      </div>
    </div>
  </div>
</template>

<script>
  export default {
    name: 'v-sign',
    data () {
      return {
        showModal: false,
        showSignIn: false,
        showSignUp: false,
        signIn: {
          remember: true,
          name: '',
          password: '',
          method: ''
        },
        signUp: {
          captcha: true,
          name: '',
          password: '',
          email: ''
        }
      }
    },
    watch: {
      showModal (val) {
        val ? this.$backdrop.show({
          ele: this.$refs.wrap
        }) : this.$backdrop.hide()
      }
    },
    created () {
      this.$channel.$on('sign-in', () => {
        this.showLogin()
      })
      this.$channel.$on('sign-up', () => {
        this.showRegister()
      })
    },
    methods: {
      showLogin () {
        this.showModal = true
        this.showSignIn = true
        this.showSignUp = false
      },
      showRegister () {
        this.showModal = true
        this.showSignUp = true
        this.showSignIn = false
        this.showCaptcha()
      },
      hiddenSign () {
        this.showModal = false
        this.showSignIn = false
        this.showSignUp = false
      },
      showCaptcha () {

      },
      login () {
        this.$toast.show('暂不开放注册')
      }
    }
  }
</script>
